import {
  ListItem,
  ListItemButton,
  IconButton,
  Card,
  CardContent,
  CardActions,
  Typography,
} from '@mui/material';
import { useState, useEffect } from 'react';
import ClappingIcon from './assets/ClappingIcon';
import AddTagButton from './AddTagButton';
import Tag from './Tag';

function Post({
  postId,
  postTitle,
  postContent,
  Tags,
  Posts,
  getPostClaps,
  addPostToTagList,
  handleAddTagClick,
  handleTagClick,
  handleClapClick,
  handleUserClapClick,
  selectedTagId,
  userId,
}) {
  const getTagsByPostId = (postID) => {
    const tagsArr = [];
    for (const tagName in Tags) {
      if (Tags[tagName][postID]) {
        tagsArr.push(tagName);
      }
    }
    return tagsArr;
  };

  const tagsNameArr = getTagsByPostId(postId);
  const isTag = tagsNameArr.length > 0 ? true : false;
  let didUserClappedOnPost = false;

  const [clapsNum, setClapsNum] = useState(0);

  useEffect(() => {
    const storedClapsNum = getPostClaps(postId);
    if (storedClapsNum) {
      setClapsNum(storedClapsNum);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem(`claps-${postId}`, JSON.stringify(clapsNum));
  }, [clapsNum]);

  return (
    <ListItem
      alignItems='flex-start'
      key={postId}
      className='post'
      data-testid={`post-${postId}`}
    >
      <Card className='post'>
        <ListItemButton disableGutters>
          <CardContent>
            <Typography
              variant='h5'
              gutterBottom
              data-testid={`postTitle-${postId}`}
            >
              {postTitle}
            </Typography>
            <Typography
              variant='body1'
              gutterBottom
              data-testid={`postContent-${postId}`}
            >
              {postContent}
            </Typography>
          </CardContent>
        </ListItemButton>
        <CardActions>
          <AddTagButton
            dataTestId={`postAddTagBtn-${postId}`}
            onClick={(e) => {
              handleAddTagClick(e, postId);
            }
          }
          />
          {isTag &&
            tagsNameArr.map((tagName) => (
              <Tag
                tagName={tagName}
                postId={postId}
                handleTagClick={handleTagClick}
                addPostToTagList={addPostToTagList}
                selectedTagId={selectedTagId}
                onClick={(e) => {
                  console.log('tag clicked');
                }}
              />
            ))}
          <IconButton
            aria-label='clapping'
            size='small'
            data-testid={`postClapsBtn-${postId}`}
            onClick={() => {
                handleUserClapClick(userId);
                handleClapClick(postId);
                setClapsNum(clapsNum + 1);
              }}
            >
            <ClappingIcon
              didUserClappedOnPost={didUserClappedOnPost}
              dataTestId={`postClappingIcon-${postId}`}
            />
          </IconButton>
          <Typography variant='string' data-testid={`postClapsNum-${postId}`}>
            {clapsNum}
          </Typography>
        </CardActions>
      </Card>
    </ListItem>
  );
}

export default Post;
