import axios from 'axios';
import './App.css';
import Home from './pages/Home';
import AddNewPost from './pages/AddNewPost';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useState, useEffect, useCallback } from 'react';
import {
  Typography,
  AppBar,
  Toolbar,
  Button,
  ButtonGroup,
  Alert,
  Snackbar,
} from '@mui/material';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import FilterAltIcon from '@mui/icons-material/FilterAlt';
import HomeIcon from '@mui/icons-material/Home';
import FloatingMenu from './components/FloatingMenu';

function App() {
  const baseURL = 'http://localhost:3080';
  const popularityOptions = [1, 5, 20, 100];

  const [userId, setUserId] = useState('');

  const [selectedPopularityQuery, setSelectedPopularityQuery] = useState('');
  const [selectedTagQuery, setSelectedTagQuery] = useState('');

  const [allPosts, setAllPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);

  const [claps, setClaps] = useState(0);
  const [clapsList, setClapsList] = useState([]);

  const [tags, setTags] = useState({});
  const [tagsList, setTagsList] = useState([]);
  const [selectedTagId, setSelectedTagId] = useState('');

  const [anchorEl, setAnchorEl] = useState(null);

  const [alertMsg, setAlertMsg] = useState('');
  const [showAlert, setShowAlert] = useState(false);
  const [alertType, setAlertType] = useState('');

  useEffect(() => {
    if (showAlert) {
      setTimeout(() => {
        handleAlert('', false, '');
      }, 1500);
    }
  }, [showAlert]);

  const handleAlert = (message, isShow, type) => {
    setAlertMsg(message);
    setShowAlert(isShow);
    setAlertType(type);
  };

  ///////////////////////////////////// data request /////////////////////////////////////
  axios.defaults.withCredentials = true;
  ///////////////////// get request /////////////////////

  // sets a userId cookie
  const getUser = useCallback(() => {
    axios
      .get(`${baseURL}/user`)
      .then((response) => {
        setUserId(response.data.id);
      })
      .catch((error) => {
        handleAlert(error.message, true, 'error');
      });
  }, []);

  const getPosts = useCallback(() => {
    axios
      .get(`${baseURL}/posts`)
      .then((response) => {
        setAllPosts([...response.data['Posts']]);
        setFilteredPosts([...response.data['Posts']]);
      })
      .catch((error) => {
        handleAlert(error.message, true, 'error');
      });
  }, []);

  const getFilteredPosts = (popularity, tag) => {
    const url = popularity !== '' ? `popularity=${popularity}` : '';
    axios
      .get(`${baseURL}/posts?${url}`)
      .then((response) => {
        setFilteredPosts([...response.data['Posts']]);
      })
      .catch((error) => {
        handleAlert(error.message, true, 'error');
      });
  };

  const getTags = useCallback(() => {
    axios
      .get(`${baseURL}/tags`)
      .then((response) => {
        setTags({ ...response.data['Tags'] });
        const tagsList = [];
        for (const tagName in response.data['Tags']) {
          tagsList.push(tagName);
        }
        setTagsList(tagsList);
      })
      .catch((error) => {
        handleAlert(error.message, true, 'error');
      });
  }, []);

  const getClaps = useCallback(() => {
    axios
      .get(`${baseURL}/claps`)
      .then((response) => {
        setClaps({ ...response.data['Claps'] });
        const clapsList = [];
        for (const clap in response.data['Claps']) {
          clapsList.push(clap);
        }
        setClaps(clapsList);
      })
      .catch((error) => {
        handleAlert(error.message, true, 'error');
      });
  }, []);      

  useEffect(() => {
    getPosts();
    getTags();
    getUser();
    getClaps();
  }, [getPosts, getTags, getUser, getClaps]);

  ///////////////////// post request /////////////////////
  const addPost = (title, content) => {
    axios
      .post(
        `${baseURL}/posts`,
        {
            title,
            content,
        },
        {
          headers: {
            // to send a request with a body as json you need to use this 'content-type'
            'content-type': 'application/x-www-form-urlencoded',
          },
        }
      )
      .then((response) => {
        const newPost = response.data['Post'];
        setAllPosts([...allPosts, newPost]);
      })
      .catch((error) => {
        handleAlert(error.message, true, 'error');
      }
      );
  };

  const addNewTag = (tagName) => {
    console.log(tagName);
    axios
      .post(`${baseURL}/tags`,
      {
        tagName
      },
      {
        headers: {
          // to send a request with a body as json you need to use this 'content-type'
          'content-type': 'application/x-www-form-urlencoded',
          }
      })
      .then((response) => {
        setTags({ ...response.data['Tags'] });
        const tagsList = [];
        for (const tagName in response.data['Tags']) {
          tagsList.push(tagName);
        }
        setTagsList(tagsList);
        handleAlert('Tag was added successfully', true, 'success');
      })
      .catch((error) => {
        handleAlert(error.message, true, 'error');
      });
  };

  const addTagToPost = (tagName, postId) => {
    console.log(tagName, postId)
    axios
      .post
      (
        `${baseURL}/posts/${postId}/tags`,
        {
          tagName,
          postId
        },
        {
          headers: {
            // to send a request with a body as json you need to use this 'content-type'
            'content-type': 'application/x-www-form-urlencoded',
          },
        }
      ).catch((error) => {
        handleAlert(error.message, true, 'error');
      });
  };

  const addPostToTagList = (tagName, postId) => {
    console.log(tagName, postId)
    axios
    .post
    (
      `${baseURL}/tags/tagName/${tagName}`,
      {
        tagName,
        postId
      },
      {
        headers: {
          // to send a request with a body as json you need to use this 'content-type'
          'content-type': 'application/x-www-form-urlencoded',
        },
      }
    ).catch((error) => {
      handleAlert(error.message, true, 'error');
    });
  };

  const handleClapClick = (postId) => {
    axios
    .post(
      `${baseURL}/posts/${postId}`,{
        postId
      },
      {
        headers: {
          // to send a request with a body as json you need to use this 'content-type'
          'content-type': 'application/x-www-form-urlencoded',
        },
      }
    )
    .catch((error) => {
      handleAlert(error.message, true, 'error');
    });        
  }

  const handleUserClapClick = (userId, clapsCount) => {
    axios
      .post(
        `${baseURL}/claps`,
        {
          userId,
          clapsCount 
        },
        {
          headers: {
            // to send a request with a body as json you need to use this 'content-type'
            'content-type': 'application/x-www-form-urlencoded',
          },
        })
      .catch((error) => {
        handleAlert(error.message, true, 'error');
      });
  };

  ///////////////////////////////////// handle click events /////////////////////////////////////
  const handlePopularityClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = (selectedOption) => {
    setAnchorEl(null);
    filterPostsByPopularity(selectedOption);
  };

  const handleHomeClick = () => {
    setFilteredPosts(allPosts);
    setSelectedPopularityQuery('');
    setSelectedTagId('');
  };

  const handleTagClick = (tagName) => {
    filterPostsByTag(tagName);
  };

  ///////////////////////////////////// filters /////////////////////////////////////
  const filterPostsByPopularity = (minClapsNum) => {
    setSelectedPopularityQuery(`${minClapsNum}`);
    getFilteredPosts(minClapsNum, selectedTagQuery);
  };

  const filterPostsByTag = (tagName) => {
    setSelectedTagQuery(tagName);
    getFilteredPosts(selectedTagQuery, tagName);

  ///////////////////////////////////// render components /////////////////////////////////////
  const renderToolBar = () => {
    return (
      <AppBar position='sticky' color='inherit'>
        <Toolbar>
          <ButtonGroup variant='text' aria-label='text button group'>
            <Button
              href='/'
              size='large'
              onClick={handleHomeClick}
              startIcon={<HomeIcon />}
            >
              Home
            </Button>
            <Button
              href='/add-new-post'
              size='large'
              startIcon={<AddCircleIcon />}
            >
              Add A New Post
            </Button>
          </ButtonGroup>
          <Typography variant='h6' component='div' sx={{ flexGrow: 1 }}>
            Enter 2023 Blog Exam
          </Typography>
          <ButtonGroup variant='text' aria-label='text button group'>
            <Button
              size='large'
              startIcon={<FilterAltIcon />}
              onClick={(e) => handlePopularityClick(e)}
              data-testid='popularityBtn'
              className={
                window.location.href !== 'http://localhost:3000/add-new-post'
                  ? ''
                  : 'visibilityHidden'
              }
            >
              filter by Popularity
            </Button>
          </ButtonGroup>
          <FloatingMenu
            menuOptions={popularityOptions}
            anchorElement={anchorEl}
            handleMenuClose={handleMenuClose}
          />
        </Toolbar>
      </AppBar>
    );
  };

  return (
    <div className='App'>
      {renderToolBar()}
      {showAlert && (
        <Snackbar open={true} data-testid='alert-snackbar'>
          <Alert severity={alertType} data-testid='alert'>
            {alertMsg}
          </Alert>
        </Snackbar>
      )}
      <Router>
        <Routes>
          <Route
            path='/add-new-post'
            element=
              {<AddNewPost 
                handleAddPost={addPost}
                tagsList={tagsList}
              />
          }
          />
          <Route
            path='/'
            element={
              <Home
                Posts={filteredPosts}
                Tags={tags}
                tagsList={tagsList}
                Claps={claps}
                clapsList={clapsList}
                addTagToPost={addTagToPost}
                addPostToTagList={addPostToTagList}
                handleTagClick={handleTagClick}
                handleAddNewTag={addNewTag}
                selectedTagId={selectedTagId}
                selectedPopularityQuery={selectedPopularityQuery}
                handleUserClapClick={handleUserClapClick}
                handleClapClick={handleClapClick}
                userId={userId}
              />
            }
          />
        </Routes>
      </Router>
    </div>
  );
}}
export default App;
