import {
  Card,
  CardContent,
  CardActions,
  Typography,
  FormControl,
  InputLabel,
  OutlinedInput,
  MenuItem,
  TextField,
  Button,
  Select,
} from '@mui/material';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AddNewPost({ handleAddPost, tagsList}) {
  const navigate = useNavigate();

  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [selectedTag, setSelectedTag] = useState('');

  const titles = ['Server', 'Client', 'Database', 'Deployment', 'Other'];

  return (
    <div className='container'>
      <Card component='form' className='form' data-testid='addNewPost-card'>
        <CardContent className='formFields'>
          <Typography
            variant='h5'
            component='div'
            className='formTitle'
            data-testid='addNewPost-title'
          >
            Add A New Post
          </Typography>
          <Typography
            gutterBottom
            variant='caption'
            component='div'
            data-testid='addNewPost-required'
          >
            *Required
          </Typography>
          <FormControl sx={{ minWidth: '100%' }}>
            <InputLabel
              required
              htmlFor='title-field'
              data-testid='addNewPost-postTitleLabel'
            >
              Title
            </InputLabel>
            <OutlinedInput
              error={false}
              id='addNewPost-postTitleInput'
              label='Title'
              fullWidth
              value={title}
              onChange={() => {
                setTitle();
              }}
              data-testid='addNewPost-postTitle'
            />
          </FormControl>
          <TextField
            id='addNewPost-postContentInput'
            label='Content'
            multiline
            rows={4}
            fullWidth
            required
            error={false}
            value={content}
            onChange={(event) => {
              setContent(event.target.value);
            }}
            data-testid='addNewPost-postContent'
          />
          <FormControl sx={{ m: 1, minWidth: 'max-content', width: '200px' }}>
            <InputLabel
              id='select-tag-label'
              data-testid='addNewPost-postTagLabel'
            >
              Tag
            </InputLabel>
            <Select
              labelId='select-tag-label'
              id='addNewPost-postTagSelect'
              value={selectedTag}
              label='Tag'
              onChange={(event) => {
                console.log(event.target.value);
                setSelectedTag(event.target.value);
              }}
              data-testid='addNewPost-postTag'
            >
              {}
            </Select>
          </FormControl>
        </CardContent>
        <CardActions>
          <Button
            onClick={() => {
              // check if the title and content are not empty
                if (title !== '' && content !== '') {
                    if(title.length < 100){
                    handleAddPost(title, content);
                    setTitle('');
                    setContent('');
                    setSelectedTag('');
                    navigate('/');
                  }
                  else{
                    alert('Title must be less than 100 characters');
                    setTitle('');
                  }
                }
                else{
                  // alert the user that the title and content are required
                  alert('Title and content are required');
                }
              }
            }
            variant='contained'
            size='large'
            data-testid='addNewPost-submitBtn'
          >
            submit
          </Button>
        </CardActions>
      </Card>
    </div>
  );
}

export default AddNewPost;
